    val data : ArrayList<Item>
        get() {
            var result = ArrayList<Item>()

            for(value in nama.indices){
                var get = Item()
                get.judul = nama[value]
                get.shipClass = shipClass[value]
                get.detail = desc[value]
                get.image = image[value]
                get.flag = flag[value]
                result.add(get)
            }

            return result
        }
}